mapper.select:
select tai_cd as "taiCd",tana_cd as "tanaCd",json,
       tana_position as "tanaPosition", result.restrict_cd as "restrictCd", area,value color
${item}
from (select restrict_cd${item}
       json_object_agg ( val, nm ) json from planocycle.work_basic_pattern_restrict_result result
INNER JOIN masterdb.planocycle_zokusei_mst_data zokuseidata ON zokuseidata.class_cd = #{classCd}
AND zokuseidata.company_cd = #{companyCd}
 val=${item}
where priority_order_cd = #{priorityOrderCd}  group by restrict_cd
${item}
) result inner join planocycle.work_basic_pattern_restrict_relation relation on result.restrict_cd=relation.restrict_cd
and relation.priority_order_cd=#{priorityOrderCd}
inner join planocycle.priority_order_color color on color.id=relation.restrict_cd
order by tai_Cd, tana_cd, tana_position
