mapper.delete:
delete from priority.work_priority_order_jan_proposal
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

delete from priority.priority_order_jan_proposal
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

