mapper.select:
select tai_cd as "taiCd",tana_cd as "tanaCd",tanaposition_cd as "tanapositionCd", jan, janmst.width as "width"
${item.zokuseiCol} as "${item.zokuseiId}"
from shelf_pts_data ptsdata inner join shelf_pts_data_jandata jandata on jandata.pts_cd=ptsdata.id inner join ${janInfoTb} janinfo on jandata.jan = janinfo."1" inner join masterdb.jan_mst janmst on janmst.jan_cd=jandata.jan where shelf_pattern_cd=#{shelfPatternCd};
