mapper.insert:
insert into ${branchInfoTableName}("1","2","3","4","5","6","100") values
(#{item.branchCd},#{item.branchName},null,null,null,#{item.areaName},'1')
on conflict("1") do nothing;

mapper.select:
select split_part( teninfo."1", '_', 1 ) "companyCd", COALESCE ( groupcompany.company_name, kigyouname."2" ) "companyName", teninfo."6" "areaName", split_part( teninfo."1", '_', 2 ) "branchCd", teninfo."2" "branchName", case when teninfo."100" ='1' then '棚割専用' else '' end "typeName" from ${branchInfoTableName} teninfo left join masterdb.group_company groupcompany on company_cd = split_part( teninfo."1", '_', 1 ) left join masterdb.kigyou_name kigyouname on kigyouname."1" = split_part( teninfo."1", '_', 1 ) where split_part( teninfo."1", '_', 1 ) in
#{item}
and teninfo."100" = '1'
