masterdb.kigyou_name name left join masterdb.kigyou_company_sortid sort on name."1" = sort."1"
left join masterdb.group_company groups on name."1" = groups.company_cd where name."1" in
#{item}
) mst
INNER JOIN masterdb.planocycle_kigyo_list list on list.company_cd = mst.group_cd
