mapper.select:
select
    shelf_pattern_cd
from planocycle.priority_order_mst
where company_cd = #{companyCd,jdbcType=VARCHAR}
    and priority_order_cd = #{priorityOrderCd}
    and deleteflg=0

select tai_cd, jan_cd,tana_cd,face,face_fact from planocycle.work_priority_all_result_data where company_cd = #{companyCd,jdbcType=VARCHAR}
and priority_all_cd = #{priorityAllCd} and author_cd=#{authorCd} and adopt_flag=1
order by tai_cd,tana_type,sort_rank,sku_rank
