mapper.select:
select count(1) from planocycle.shelf_pts_data pts inner join planocycle.product_power_mst prod on pts.company_cd = prod.company_cd left join masterdb.group_company grpcom on pts.company_cd = grpcom.group_cd where coalesce(grpcom.company_cd,pts.company_cd) in (
#{list}
) and pts.shelf_pattern_cd is not null

SELECT array_to_string(array_agg(priority_order_cd),',')
FROM planocycle.priority_order_mst
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}
group by product_power_cd

mapper.delete:
delete from planocycle.priority_order_mst
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

delete from planocycle.priority_order_mst
where company_cd = #{companyCd,jdbcType=VARCHAR}
and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER} and author_cd=#{authorCd,jdbcType=VARCHAR}

mapper.update:
update planocycle.priority_order_mst set deleteflg=1
where company_cd = #{companyCd,jdbcType=VARCHAR} and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}
and author_cd=#{authorCd,jdbcType=VARCHAR}

mapper.insert:
insert into planocycle.priority_order_mst (company_cd, priority_order_cd,
product_power_cd,shelf_pattern_cd,attribute1,attribute2,partition_flag,partition_val,author_cd,editer_cd,
edit_time,create_time, priority_order_name)
select company_cd, priority_order_cd,
product_power_cd,shelf_pattern_cd,attribute1,attribute2,partition_flag,partition_val,author_cd,author_cd, now(),now(),#{priorityOrderName}
from planocycle.work_priority_order_mst
where company_cd = #{companyCd,jdbcType=VARCHAR} and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}
and author_cd=#{authorCd,jdbcType=VARCHAR}
