mapper.update:
update priority.priority_order_pts_jandata

    rank=
    CASE jan_cd

        WHEN trim(#{obj.jan}) THEN #{obj.tanapositionCd}

    ELSE rank END


    company_cd=#{companyCd} and priority_order_cd=#{priorityOrderCd}

mapper.select:
select jan_new jan, ROW_NUMBER() OVER(PARTITION by
${obj}
 order by case "orderdata".rank when -1 then -1 else 0 end,jandata.rank,rank_prop, "orderdata".rank) rank
from priority.work_priority_order_result_data "orderdata" inner join
priority.priority_order_pts_jandata jandata on jandata.jan_cd="orderdata".jan_new and jandata.priority_order_cd=orderdata.priority_order_cd
where jandata.company_cd=#{companyCd} and jandata.priority_order_cd=#{priorityOrderCd} and flg!=99999999;

mapper.update:
update priority.work_priority_order_result_data
set rank_upd=
CASE jan_new

    WHEN trim(#{obj.jan}) THEN #{obj.rank}

ELSE rank_upd END
where rank_upd != 99999999 and priority_order_cd=#{priorityOrderCd}

update priority.work_priority_order_result_data
set
${attr1}=CASE jan_new

    WHEN trim(#{obj.jan}) THEN #{obj.attr1}

ELSE ${attr1} END,
${attr2}=CASE jan_new

    WHEN trim(#{obj.jan}) THEN #{obj.attr2}

ELSE ${attr2} END
where priority_order_cd=#{priorityOrderCd}

mapper.select:
select tmp.jan_cd jan, classify.attr1 , classify.attr2,tmp.tanaposition_cd,#{companyCd} company_cd,#{priorityOrderCd} priority_order_cd, tmp.tai_cd tai_cd, tmp.tana_cd tana_cd from (select * from priority.priority_order_pts_jandata jandata where flg != 99999999 and priority_order_cd=#{priorityOrderCd} and company_cd=#{companyCd}) jandata right join(
select #{obj.jan} jan_cd,#{obj.taiCd} tai_cd,#{obj.tanaCd} tana_cd,#{obj.tanapositionCd} tanaposition_cd
) tmp on jandata.jan_cd=tmp.jan_cd inner join (select * from priority.work_priority_order_attribute_classify where priority_order_cd=#{priorityOrderCd} and company_cd=#{companyCd} ) classify on classify.tai_cd=tmp.tai_cd and classify.tana_cd=tmp.tana_cd where jandata.jan_cd is null

mapper.update:
update priority.priority_order_pts_jandata
set flg=99999999
where priority_order_cd=#{priorityOrderCd} and company_cd=#{companyCd}
and jan_cd in

    #{obj.jan}
