mapper.select:
select 

from priority_order_restrict_relation
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

mapper.delete:
delete from priority_order_restrict_relation
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

mapper.update:
update priority_order_restrict_relation


    tai_cd = #{taiCd,jdbcType=INTEGER},


    tana_cd = #{tanaCd,jdbcType=INTEGER},


    restrict_cd = #{restrictCd,jdbcType=BIGINT},


where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

update priority_order_restrict_relation
set tai_cd = #{taiCd,jdbcType=INTEGER},
  tana_cd = #{tanaCd,jdbcType=INTEGER},
  restrict_cd = #{restrictCd,jdbcType=BIGINT}
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

update planocycle.priority_order_restrict_relation
set deleteflg=1
where company_cd = #{companyCd,jdbcType=VARCHAR} and author_cd=#{authorCd,jdbcType=VARCHAR}
and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

mapper.insert:
insert into planocycle.priority_order_restrict_relation(company_cd,priority_order_cd,tai_cd,tana_cd,tana_type,restrict_cd,
author_cd)
select company_cd,priority_order_cd,tai_cd,tana_cd,tana_type,restrict_cd,
author_cd from planocycle.work_priority_order_restrict_relation
where company_cd = #{companyCd,jdbcType=VARCHAR} and author_cd=#{authorCd,jdbcType=VARCHAR}
and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}
