mapper.select:
select 

from priority_order_restrict_set
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}
  and tai_cd = #{taiCd,jdbcType=INTEGER}
  and tana_cd = #{tanaCd,jdbcType=INTEGER}
  and restrict_type = #{restrictType,jdbcType=INTEGER}

mapper.delete:
delete from priority_order_restrict_set
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}
  and tai_cd = #{taiCd,jdbcType=INTEGER}
  and tana_cd = #{tanaCd,jdbcType=INTEGER}
  and restrict_type = #{restrictType,jdbcType=INTEGER}

mapper.update:
update priority_order_restrict_set


    category = #{category,jdbcType=INTEGER},


    pkg = #{pkg,jdbcType=INTEGER},


    capacity = #{capacity,jdbcType=INTEGER},


    author_cd = #{authorCd,jdbcType=VARCHAR},


    create_time = #{createTime,jdbcType=TIMESTAMP},


    editer_cd = #{editerCd,jdbcType=VARCHAR},


    edit_time = #{editTime,jdbcType=TIMESTAMP},


    deleteflg = #{deleteflg,jdbcType=SMALLINT},


where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}
  and tai_cd = #{taiCd,jdbcType=INTEGER}
  and tana_cd = #{tanaCd,jdbcType=INTEGER}
  and restrict_type = #{restrictType,jdbcType=INTEGER}

update priority_order_restrict_set
set category = #{category,jdbcType=INTEGER},
  pkg = #{pkg,jdbcType=INTEGER},
  capacity = #{capacity,jdbcType=INTEGER},
  author_cd = #{authorCd,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  editer_cd = #{editerCd,jdbcType=VARCHAR},
  edit_time = #{editTime,jdbcType=TIMESTAMP},
  deleteflg = #{deleteflg,jdbcType=SMALLINT}
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}
  and tai_cd = #{taiCd,jdbcType=INTEGER}
  and tana_cd = #{tanaCd,jdbcType=INTEGER}
  and restrict_type = #{restrictType,jdbcType=INTEGER}

mapper.insert:
insert into planocycle.priority_order_restrict_set(company_cd,priority_order_cd,tai_cd,tana_cd,restrict_type,category,pkg,capacity,author_cd,create_time)
values (#{item.companyCd},#{item.priorityOrderCd},#{item.taiCd},#{item.tanaCd},#{item.restrictType},#{item.category},#{item.pkg},#{item.capacity}
,#{authorCd},now())
on conflict(company_cd,priority_order_cd,tai_cd,tana_cd,restrict_type)
do update set
    editer_cd = #{authorCd},edit_time = now()

        ,pkg = #{item.pkg}


        ,category = #{item.category}


        ,capacity=#{item.capacity}
