mapper.update:
update planocycle.product_power_syokika set deleteflg=1,editer_cd=#{authorCd},edit_time=now()
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

update planocycle.product_power_kokyaku set deleteflg=1,editer_cd=#{authorCd},edit_time=now()
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

update planocycle.product_power_reserve_data set deleteflg=1,editer_cd=#{authorCd},edit_time=now()
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

update planocycle.product_power_reserve_mst set deleteflg=1,editer_cd=#{authorCd},edit_time=now()
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

update planocycle.product_power_mst_data set deleteflg=1,editer_cd=#{authorCd},edit_time=now()
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

mapper.select:
select s.jan as jan,s.sku_name as skuName,k.pos_amount as gPosAmount,k.pos_num as gPosNum,k.branch_amount as gBranchAmount, k.branch_num as gBranchNum,k.compare_amount as gCompareAmount,k.compare_num as gCompareNum,k.branch_compare_amount as gBranchCompareAmount, k.branch_compare_num as gBranchCompareNum,s.classify_big as classifyBig,s.classify_middle as classifyMiddle,s.classify_small as classifySmall, s.classify_fine as classifyFine,s.pos_amount as pPosAmount,s.pos_num as pPosNum,s.branch_amount as pBranchAmount,s.branch_num as pBranchNum, s.compare_amount as pCompareAmount,s.compare_num as pCompareNum, s.branch_compare_amount as pBranchCompareAmount,s.branch_compare_num as pBranchCompareNum, yobi.item1,yobi.item2,yobi.item3,yobi.item4,yobi.item5,yobi.item6,yobi.item7,yobi.item8,yobi.item9,yobi.item10
from planocycle.work_product_power_syokika s left join planocycle.work_product_power_kokyaku k on k.jan = s.jan and k.author_cd=s.author_cd and s.company_cd = k.company_cd left join (select dd.jan as jan,
sum ( case when dd.data_cd = '31001' then dd.data_value else 0 end ) as item1, sum ( case when dd.data_cd = '31002' then dd.data_value else 0 end ) as item2, sum ( case when dd.data_cd = '31003' then dd.data_value else 0 end ) as item3, sum ( case when dd.data_cd = '31004' then dd.data_value else 0 end ) as item4, sum ( case when dd.data_cd = '31005' then dd.data_value else 0 end ) as item5, sum ( case when dd.data_cd = '31006' then dd.data_value else 0 end ) as item6, sum ( case when dd.data_cd = '31007' then dd.data_value else 0 end ) as item7, sum ( case when dd.data_cd = '31008' then dd.data_value else 0 end ) as item8, sum ( case when dd.data_cd = '31009' then dd.data_value else 0 end ) as item9, sum ( case when dd.data_cd = '31010' then dd.data_value else 0 end ) as item10 from planocycle.work_product_power_reserve_mst mm inner join planocycle.work_product_power_reserve_data dd on mm.data_cd = dd.data_cd where mm.author_cd = #{authorCd} and mm.company_cd = #{companyCd} GROUP BY dd.jan )as yobi on yobi.jan = s.jan where s.author_cd=#{authorCd} and s.company_cd = #{companyCd} order by jan

select count(1) from planocycle.product_power_syokika
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

select * from planocycle.product_power_syokika
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER} and deleteflg=0

select count(1) from planocycle.product_power_kokyaku
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

select count(1) from planocycle.product_power_yobiitem
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

SELECT max(data_sort)
 from  planocycle.work_product_power_reserve_mst
where company_cd=#{companyCd} and author_cd = #{aud}

select data_cd,data_name from planocycle.product_power_reserve_mst
where company_cd=#{companyCd} and product_power_cd = #{productPowerCd}
order by data_sort asc

select power_mst.jan,

   jan_info."${item.sort}" as ${item.attr_val}

from planocycle.product_power_mst_data power_mst
inner join ${tableName} jan_info on power_mst.jan=jan_info.${janCdColName}
where power_mst.company_cd=#{companyCd} and power_mst.product_power_cd=#{productPowerCd}

select count(1)
from planocycle.product_power_param_mst
where company_cd=#{company} and product_power_cd =#{productPowerNo}  and deleteflg=0
  and store_cd = #{storeCd}
  and prd_cd = #{prdCd}
  and recently_end_time = #{recentlyEndTime}
  and recently_flag = #{recentlyFlag}
  and recently_st_time = #{recentlyStTime}
  and season_flag = #{seasonFlag}
  and season_end_time = #{seasonEndTime}
  and season_st_time = #{seasonStTime}

select power_syokika.*,

    jan_info."${item.sort}" as ${item.attr_val}

from planocycle.work_product_power_syokika power_syokika
left join ${tableName} jan_info on power_syokika.jan=jan_info.${janCdColName}
where power_syokika.author_cd = #{authorCd} and power_syokika.company_cd = #{companyCd}
