mapper.select:
select

from planocycle.product_power_param_mst
where company_cd = #{conpanyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}

mapper.insert:
insert into planocycle.product_power_param_mst (company_cd, product_power_cd, perid_flag,
  start_perid, end_perid,season_perid_flag, season_st_perid,
  season_end_perid,  store_cd,
  channel_name, county_name, item_flg,prod_cd
  )
values (#{conpanyCd,jdbcType=VARCHAR}, #{productPowerCd,jdbcType=INTEGER}, #{peridFlag,jdbcType=INTEGER},
  #{startPerid,jdbcType=TIMESTAMP}, #{endPerid,jdbcType=TIMESTAMP}, #{seasonPeridFlag,jdbcType=INTEGER}, #{seasonStPerid,jdbcType=TIMESTAMP},
  #{seasonEndPerid,jdbcType=TIMESTAMP}, #{storeCd,jdbcType=VARCHAR},
  #{channelName,jdbcType=VARCHAR}, #{countyName,jdbcType=VARCHAR}
         , #{itemFlg,jdbcType=INTEGER},#{prdCd,jdbcType=VARCHAR}
  )

mapper.delete:
delete from planocycle.product_power_param_mst
where company_cd = #{conpanyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}
  and (perid_flag != #{peridFlag,jdbcType=INTEGER}
        or start_perid != #{startPerid,jdbcType=TIMESTAMP}
         or end_perid != #{endPerid,jdbcType=TIMESTAMP}
         or season_perid_flag != #{seasonPeridFlag,jdbcType=INTEGER}
         or season_st_perid != #{seasonStPerid,jdbcType=TIMESTAMP}
         or season_end_perid != #{seasonEndPerid,jdbcType=TIMESTAMP}
         or store_cd != #{storeCd,jdbcType=VARCHAR}
         or channel_name != #{channelName,jdbcType=VARCHAR}
         or county_name != #{countyName,jdbcType=VARCHAR}
        or prod_cd != #{prdCd,jdbcType=VARCHAR})

delete from planocycle.product_power_param_mst
where company_cd = #{conpanyCd,jdbcType=VARCHAR}
  and product_power_cd = #{productPowerCd,jdbcType=INTEGER}
