mapper.select:
select

from planocycle.product_power_show_mst
where product_power_cd = #{productPowerCd,jdbcType=INTEGER}
  and company_cd = #{conpanyCd,jdbcType=VARCHAR}

mapper.delete:
delete from planocycle.product_power_show_mst
where product_power_cd = #{productPowerCd,jdbcType=INTEGER}
  and company_cd = #{conpanyCd,jdbcType=VARCHAR}
