mapper.insert:
insert into priority.work_priority_order_pts_classify(company_cd,priority_order_cd,pts_cd,sku_num,pts_branch_num,sku_num_init,
${item}
) select #{companyCd},#{priorityOrderCd}, pts_cd, count(DISTINCT jandata.jan) as sku_num ,count(DISTINCT branch) as branch_num,count(DISTINCT jandata.jan) as sku_num,
mst.${item}
from planocycle.shelf_pts_data_jandata jandata left join priority.work_priority_order_result_data mst on mst.jan_old = jandata.jan INNER join planocycle.shelf_pts_data ptsdata on jandata.pts_cd = ptsdata."id" INNER join planocycle.shelf_pattern_branch branch on branch.shelf_pattern_cd = ptsdata.shelf_pattern_cd where jandata.pts_cd in
#{item.id}
GROUP BY pts_cd,
mst.${item}
