mapper.delete:
delete from planocycle.work_priority_order_restrict_result
where company_cd = #{companyCd,jdbcType=VARCHAR} and author_cd = #{authorCd,jdbcType=VARCHAR}
  and priority_order_cd = #{priorityOrderCd,jdbcType=INTEGER}

mapper.select:
select restrict_cd,zokusei1,zokusei2,zokusei3,zokusei4,zokusei5,zokusei6,zokusei7,zokusei8,zokusei9,zokusei10,sku_cnt
from planocycle.work_priority_order_restrict_result
where company_cd = #{companyCd} and author_cd = #{authorCd} and priority_order_cd = #{priorityOrderCd}

select distinct  mst.jan,mst.jan_new,mst.rank_result,mst.jan_rank as rankNewResult
from ( select  mstData.jan,mstData.sku_name,replaces.jan_new,janMst.brand,
janMst.kikaku,janMst.pkg,janMst.irisu,janMst.alcohol,janMst.scat1cd_val,janMst.scat2cd_val,janMst.scat3cd_val,janMst.scat4cd_val,mstData.rank_result,janNews.jan_rank
from
planocycle.product_power_mst_data  mstData
left join masterdb.jan_mst janMst
on  janMst.jan_cd = mstData.jan
left join planocycle.work_priority_order_jan_replace replaces
on replaces.jan_old = mstData.jan and replaces.author_cd = #{authorCd} and replaces.company_cd = #{companyCd} and  replaces.priority_order_cd = #{item.priorityOrderCd}
left join planocycle.work_priority_order_jan_new janNews
on janNews.jan_new = mstData.jan and janNews.author_cd = #{authorCd} and janNews.company_cd = #{companyCd} and  janNews.priority_order_cd = #{item.priorityOrderCd}
where
mstData.company_cd = #{companyCd} and mstData.product_power_cd=#{productPowerCd}
and
not EXISTS(select * from  planocycle.work_priority_order_cut card where mstData.jan = card.jan_cd and card.author_cd = #{authorCd} and card.company_cd = #{companyCd}
    and card.priority_order_cd = #{item.priorityOrderCd})

union
select janNew.jan_new,janMst.jan_nm,null,janMst.brand,
janMst.kikaku,janMst.pkg,janMst.irisu,janMst.alcohol,janMst.scat1cd_val,janMst.scat2cd_val,janMst.scat3cd_val,janMst.scat4cd_val,janNew.jan_rank,null
from
planocycle.work_priority_order_jan_new janNew
left join masterdb.jan_mst janMst
on  janMst.jan_cd = janNew.jan_new
    where  janNew.author_cd = #{authorCd} and janNew.company_cd = #{companyCd} and janNew.priority_order_cd = #{item.priorityOrderCd}
and not Exists(select jan_cd from planocycle.product_power_mst_data datas where	datas.jan = janNew.jan_new )
    ) mst


    and  mst.scat1cd_val = #{item.zokusei1}


    and  mst.scat2cd_val = #{item.zokusei2}


    and  mst.scat3cd_val = #{item.zokusei3}


    and  mst.scat4cd_val = #{item.zokusei4}


    and  mst.pkg = #{item.zokusei5}


    and  mst.kikaku = #{item.zokusei6} :: integer


    and  mst.irisu = #{item.zokusei7} :: integer


    and  mst.alcohol = #{item.zokusei8}


    and  mst.brand = #{item.zokusei9}


    and  mst.brand = #{item.zokusei10}


order by rank_result,jan
limit #{item.skuCnt}
